package threejava;

public class EXMPLE_ARRAY {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int[] array = {1, 2, 3, 4, 5};
	        int sum = 0;

	        for (int num : array) {
	            sum += num;
	        }

	        System.out.println("The sum of the array values is: " + sum);
		
	}

}
